        }else{
            return $this->respuesta("Error", 404, "Not found");
        }
        
        

    }
    
    //Método que responde para cada mailing de modelo
    public function responseMail($status, $datos){
        
        $data = array(
            "status"    => $status,
            "code"      => 200,
            "data"      => $datos
        );
        
        return $this->json($data);
        
    }
    
   
    //Método que respondera json
    public function respuesta($status, $codigo, $msg){
        $data = array(
            "status" => $status,
            "code" => $codigo,
            "message" => $msg
        );

        return $this->json($data);
    }
    
}
